using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Web;
using AzureSiteReplicator.Contracts;
using AzureSiteReplicator.Management;

namespace AzureSiteReplicator.Data
{
    public class PublishXmlRepository : IPublishXmlRepository
    {
        public string RootDirectory
        {
            get;
            set;
        }

        public IEnumerable<Site> Sites { get; private set; }
        
        public PublishXmlRepository()
        {
            var profile = new PublishProfile
            {
                SchemaVersion = "2.0",
                PublishMethod = "AzureServiceManagementAPI",
                Subscriptions = new PublishProfile.Subscription[]
                {
                    new PublishProfile.Subscription
                    {
                        Id = "b104f328-8cc0-4fb6-bade-cfe78a6d3ae6",
                        ManagementCertificate =
                            "",
                        ServiceManagementUrl = "https://management.core.windows.net",
                        Name = "Visual Studio Ultimate with MSDN"
                    }
                }
            };

            var sites = new List<Site>();

            foreach (WebSite site in WebSite.GetAllAsync().Result)
            {
                var xml = site.GetPublishXml().Result;
                RootDirectory = Path.Combine(Environment.Instance.SiteReplicatorPath, "PublishXml");
                Directory.CreateDirectory(RootDirectory);
                var path = Path.Combine(RootDirectory, site.Name + ".publishSettings");
                File.WriteAllText(path, xml);
                sites.Add(new Site(path));
            }

            Sites = sites;
        }
    }
}